var link=document.querySelector(".open-feedback"),popup=document.querySelector(".feedback-overlay"),close=popup.querySelector(".feedback-close"),login=popup.querySelector("[name=name]"),form=popup.querySelector("form"),email=popup.querySelector("[name=mail]"),textfield=popup.querySelector("[name=text]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){e.preventDefault(),login.value&&email.value&&textfield.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"),console.log("Нужно ввести логин и пароль"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error")))});
